void jswrap_pb_writeDACReg(int reg, int value) {
#ifndef LINUX
  if (jshPinGetValue(SD_POWER_PIN)==0) {
    jsExceptionHere(JSET_ERROR, "Can't write DAC register when it is powered off");
    return;
  }
  es8388_write_reg(reg & 0xFF, value & 0xFF); // DAC mute
  es8388_check_err();
#endif
}

