    es8388_write_reg(0x19, 0x00); // Unmute DAC (no volume soft ramp, 0x32 uses ramp)
    es8388_write_reg(0x04, 0x3C); // Power up DAC, Analog out
    es8388_write_reg(0x02, 0xAA); // power up DEM and STM
